BuybidGui.register_template('product-slide', {
  name: 'product-slide',
  render: function(product) {
    return /*html*/`
      <!--generated by products.js-->
      <div class='product-item' data-hook='products_list_item'>
        <div class='product'>
          <a href='/products/${product.slug}' title='${product.name}'>
            <div class='product_image'>
              <div class='product_image-overlay'></div>
              <div class='frontpage_square'>
                  <img class='image-center' src='${product.image_url}'>
              </div>
            </div>
             ${
              product.trends === 'hots'
              ?`<div class="product_bubble product_bubble_right product_bubble_red">
                <span>${BuybidFrontend.BusinessNamesDisplay[product.business_name.toUpperCase()]}</span>
              </div>`
              : ''
            }
            <div class='product_seller'>
              <div class='product_seller-img'>
                  <img class='image-center' src='${product.seller_image_url}'>
              </div>
              <div class='product_seller-name'>${product.seller_name}</div>
            </div>
            <div class='product_info'>
              <h6 class='product_name'><span class='info' itemprop='name'>${product.name}</span></h6>
              <div class='product_price'>
                ${product.price_jpy}
                <span class='product_price_vnd'>${product.price_vnd}</span>
              </div>
            </div>
          </a>
        </div>
      </div>`;
  }
});

BuybidGui.register_template('product-grid', {
  name: 'product-grid' ,
  render: function(product) {
    return /*html*/`
      <div class="product-item">
        <div class="product">
            <a href=${product.permalink} title=${product.name}>
                <div class="product_image">
                    <div class="frontpage_square">
                        <img class="lazy"
                            data-src=${product.image_url}
                            src=${product.image_url} 
                            alt=${product.name}>
                    </div>
                </div>
            </a>
            <div class="product_bubble product_bubble_right product_bubble_red">
                <span>${product.trend}</span>
            </div>

            <div class="product_info">
                <div class="product_time"><i class="far fa-clock"></i>
                  ${product.expired_in}
                </div>

                <a href=${product.permalink} title=${product.name}
                    class="product_name">${product.name}</a>

                <div class="product_price">
                    <span class="product_price_jpy">
                        ${product.price_jpy}
                        <small class="product_price_sale">${product.price_jpy}</small></span>
                    <span class="product_price_vnd">${product.price_vnd}</span>
                </div>

                <div class="product_review">
                    <div class="product_rating">
                        <div class="rating-range">
                            <!-- Số điểm * 14px -->
                            <div class="rating-selected"
                                style="width: calc(5.00 * 14px);"></div>
                        </div>
                    </div>
                    <div class="product_bids">
                        <i class="fa fa-gavel"></i> ${product.bids}
                    </div>
                </div>
            </div>

            <div class="product_more" >

                <a href="#" class="product_seller" title=${product.seller_name}>
                    <div class="product_seller-img search">
                        <img src=${product.seller_image_url}  class="image-center" alt=${product.seller_name}>
                    </div>
                    <div class="product_seller-name">${product.seller_name}</div>
                </a>

                <div class="product_action">
                    <a class="btn product_action-btn" href="#" title="Yêu thích">
                        <span class="fa fa-heart"></span>
                    </a>
                </div>

            </div>

        </div>      

      </div>`
  }
});